import React, { useState } from 'react';
import Message from './Message';
import './styles/Chatbot.css';

function Chatbot() {
  const [messages, setMessages] = useState([
    { text: "Hello! I am your sophisticated chatbot. How can I assist you today?", sender: "bot" }
  ]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [hideTyping, setHideTyping] = useState(false);
  const [correctionIndex, setCorrectionIndex] = useState(null);
  const [correctionText, setCorrectionText] = useState("");

  const handleSend = async () => {
    if (!input.trim()) return;

    // Add user message
    const newMessages = [...messages, { text: input, sender: 'user', timestamp: new Date() }];
    setMessages(newMessages);
    setInput("");

    // Show typing indicator
    setIsTyping(true);
    setHideTyping(false);

    // Simulate bot response
    setTimeout(() => {
      const botReply = "This is a smart response generated by the backend system.";
      setMessages(prev => [
        ...prev,
        {
          text: botReply,
          sender: 'bot',
          timestamp: new Date(),
          onFirstSentence: () => {
            setHideTyping(true);
            setTimeout(() => setIsTyping(false), 500);
          }
        }
      ]);
    }, 1500);
  };

  // Handle feedback buttons
  const handleFeedback = (type, messageText, index) => {
    if (type === 'y') {
      console.log("Approved:", messageText);
      // TODO: call backend to save approved response
    } else if (type === 'n') {
      setCorrectionIndex(index);
      setCorrectionText("");
    }
  };

  // Submit correction
  const submitCorrection = (index) => {
    console.log("Correction submitted:", correctionText);
    // TODO: send correctionText + original bot message to backend
    setCorrectionIndex(null);
    setCorrectionText("");
  };

  return (
    <div className="chatbot-container">
      <div className="chat-header">üí¨ Topia's AI Wellness Assistant</div>
      <div className="chat-window">
        {messages.map((msg, index) => (
          <div key={index}>
            <Message
              text={msg.text}
              sender={msg.sender}
              timestamp={msg.timestamp}
              onFirstSentence={msg.onFirstSentence}
            />

            {/* Feedback buttons only for bot */}
            {msg.sender === 'bot' && (
              <div className="feedback">
                <button onClick={() => handleFeedback('y', msg.text, index)}>üëç</button>
                <button onClick={() => handleFeedback('n', msg.text, index)}>‚úèÔ∏è</button>
              </div>
            )}

            {/* Inline correction box */}
            {correctionIndex === index && (
              <div className="correction-box">
                <input
                  type="text"
                  value={correctionText}
                  onChange={(e) => setCorrectionText(e.target.value)}
                  placeholder="Suggest a better response..."
                />
                <button onClick={() => submitCorrection(index)}>‚úîÔ∏è</button>
              </div>
            )}
          </div>
        ))}

        {/* Typing Indicator */}
        {isTyping && (
          <div className={`typing-indicator ${hideTyping ? "hidden" : ""}`}>
            <div className="dot"></div>
            <div className="dot"></div>
            <div className="dot"></div>
          </div>
        )}
      </div>

      <div className="chat-input-area">
        <input
          type="text"
          className="chat-input"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
        />
        <button className="send-button" onClick={handleSend}>‚û§</button>
      </div>
    </div>
  );
}

export default Chatbot;
